-- DayChongZhiModel.lua-- created by chj 2015-3-10-- 每日充值DayChongZhiModel = {}-- 活动idDayChongZhiModel.ACTIVITY_ID = 13DayChongZhiModel.ACTIVITY_CHILD_ID = 4DayChongZhiModel.data_t = {	yuanbao_cz = 0,	is_get_gift = 0,}function DayChongZhiModel:fini( )end-- 设置活动idfunction DayChongZhiModel:set_act_id( act_id )	DayChongZhiModel.ACTIVITY_ID = act_idendfunction DayChongZhiModel:get_data( )	return self.data_tend-- 获取活动时间function DayChongZhiModel:get_acti_time()	local time = SmallOperationModel:get_act_time( DayChongZhiModel.ACTIVITY_ID); 	return timeend-- 请求面板数据function DayChongZhiModel:req_acti_info( )	OnlineAwardCC:req_activity_data_com( DayChongZhiModel.ACTIVITY_ID, DayChongZhiModel.ACTIVITY_CHILD_ID )end-- 请求领取礼包function DayChongZhiModel:req_get_gift()	OnlineAwardCC:req_get_activity_award_com( DayChongZhiModel.ACTIVITY_ID, DayChongZhiModel.ACTIVITY_CHILD_ID, nil, nil )end-- 服务器放回数据，刷新modelfunction DayChongZhiModel:set_data( activity_data)	self.data_t.yuanbao_cz = activity_data.had_get_record	self.data_t.is_get_gift = activity_data.can_get_record	print("------data_t:", self.data_t.yuanbao_cz, self.data_t.is_get_gift )	local win = UIManager:find_visible_window("day_chongzhi")	if win then		win:update("init")	endend-- 获取道具配置function DayChongZhiModel:get_item_conf( )	return DayChongZhiConfig:get_item_conf( )endfunction DayChongZhiModel:get_chongzhi_yuanbao( )	return DayChongZhiConfig:get_chongzhi_yuanbao( )end