-- DayChongZhiMultiModel.lua-- created by chj 2015-3-10-- 每日充值（多档次）DayChongZhiMultiModel = {}-- 活动idDayChongZhiMultiModel.ACTIVITY_ID = 25DayChongZhiMultiModel.ACTIVITY_CHILD_ID = 9DayChongZhiMultiModel.data_t = {	yuanbao_cz = 0,	act_data = nil,}function DayChongZhiMultiModel:fini( )end-- 设置活动idfunction DayChongZhiMultiModel:set_act_id( act_id )	DayChongZhiMultiModel.ACTIVITY_ID = act_idendfunction DayChongZhiMultiModel:get_data( )	return self.data_tend-- 获取活动时间function DayChongZhiMultiModel:get_acti_time()	local time = SmallOperationModel:get_act_time( DayChongZhiMultiModel.ACTIVITY_ID); 	return timeend-- 请求面板数据function DayChongZhiMultiModel:req_acti_info( )	OnlineAwardCC:req_activity_data_com( DayChongZhiMultiModel.ACTIVITY_ID, DayChongZhiMultiModel.ACTIVITY_CHILD_ID )end-- 请求领取礼包function DayChongZhiMultiModel:req_get_gift( index)	OnlineAwardCC:req_get_activity_award_com( DayChongZhiMultiModel.ACTIVITY_ID, DayChongZhiMultiModel.ACTIVITY_CHILD_ID, index, nil )end-- 服务器放回数据，刷新modelfunction DayChongZhiMultiModel:set_data( activity_data)	self.data_t.yuanbao_cz = activity_data.arg	self.data_t.act_data = activity_data	print("------data_t:", self.data_t.yuanbao_cz, self.data_t.act_data )	local win = UIManager:find_visible_window("day_cz_multi_win")	if win then		win:update("init")	endend-- 获取道具配置function DayChongZhiMultiModel:get_item_conf( )	return DayChongZhiConfig:get_item_conf( )endfunction DayChongZhiMultiModel:get_chongzhi_yuanbao( )	return DayChongZhiConfig:get_chongzhi_yuanbao( )end