-- SeriesChongZhiModel.lua-- created by chj 2015-3-10-- 连续充值SeriesChongZhiModel = {}-- 按钮状态SeriesChongZhiModel.NOT_GET = 0  -- 不可领取SeriesChongZhiModel.CAN_GET = 1  -- 可领取SeriesChongZhiModel.ALD_GET = 2  -- 已领取-- 活动idSeriesChongZhiModel.ACTIVITY_ID = 14SeriesChongZhiModel.ACTIVITY_CHILD_ID = 4SeriesChongZhiModel.data_t = {	num_yb = 0,	num_day = 0,	-- 1：可领取，0：不可以领取，2：已领取	-- [0] = {1,0,2},	btn_state ={		[1] = {},		[2] = {},		[3] = {},	}}function SeriesChongZhiModel:fini( )endfunction SeriesChongZhiModel:get_data( )	return self.data_tend-- 获取活动剩余时间function SeriesChongZhiModel:get_acti_remain_time()	local time = SmallOperationModel:get_act_time( SeriesChongZhiModel.ACTIVITY_ID)	return timeend-- 获取活动时间function SeriesChongZhiModel:get_acti_time()	local time_str= SmallOperationModel:getActivityTimeDescEx( SeriesChongZhiModel.ACTIVITY_ID)	return time_strend-- 请求面板数据function SeriesChongZhiModel:req_acti_info( )	OnlineAwardCC:req_seriescz_info( )end-- 请求领取礼包function SeriesChongZhiModel:req_get_gift( num_day, index)	OnlineAwardCC:req_seriescz_get_gift( num_day, index)end-- 服务器放回数据，刷新modelfunction SeriesChongZhiModel:set_data( num_yb, num_day, gift_3_t, gift_5_t, gift_7_t)	self.data_t.num_yb = num_yb	self.data_t.num_day = num_day	self.data_t.btn_state[1] = gift_3_t	self.data_t.btn_state[2] = gift_5_t	self.data_t.btn_state[3] = gift_7_t	local win = UIManager:find_visible_window("series_chongzhi")	if win then		win:update("init")	endend-- 获取道具配置function SeriesChongZhiModel:get_conf( )	return SeriesChongZhiConfig:get_conf( )end