-- TehuiModel.lua-- created by liuguowang on 2014-4-10-- 特惠狂欢信息模型 动态数据-- super_class.TehuiModel()TehuiModel = {}local _libao_data = {} --5个按钮状态local _zhizun_data = {} local _index = 0local _libao_num = 0local _can_get_times = 0local _person_num = 0local _currentActivityId = nil-- added by aXIng on 2013-5-25function TehuiModel:fini( ... )	_libao_data = {}	_zhizun_data = {}	_index = 0	_libao_num = 0	_can_get_times = 0	_person_num = 0	_currentActivityId = nilend--function TehuiModel:set_libao_data(index,libao_num,libao_data )--左侧的数据	_index = index	_libao_num = libao_num	_libao_data = libao_dataend--function TehuiModel:get_libao_data()	print("_libao_data=",_libao_data)	return _index,_libao_num,_libao_dataend--function TehuiModel:set_zhizun_data( can_get_times,person_num,zhizun_data)--右侧的数据	_can_get_times = can_get_times	_person_num = person_num	_zhizun_data = zhizun_dataend--function TehuiModel:get_zhizun_data(  )	print("#_zhizun_data=",#_zhizun_data)	return _can_get_times,_person_num,_zhizun_dataendfunction TehuiModel:setActivityId(activityId)	_currentActivityId = activityIdendfunction TehuiModel:getActivityId()	return _currentActivityIdendfunction TehuiModel:getCurrentConfig()	local t_currentConfig = nil	if _currentActivityId == 51 then		require "../data/activity_config/tehui_config"		t_currentConfig = _tehui_slot_id	elseif _currentActivityId == 94 then		require "../data/activity_config/tehuiex_config"		t_currentConfig = _tehui_slot_id	elseif _currentActivityId == 98 then		require "../data/activity_config/tehuimax_config"		t_currentConfig = _tehui_slot_id	elseif _currentActivityId == 115 then		require "../data/activity_config/tehuimax4_config"		t_currentConfig = _tehui_slot_id	end	return t_currentConfigend